import React, { useContext, useEffect } from 'react'
import { useNavigate, useParams } from 'react-router-dom'
import { BookContext } from '../cAPI'
import BookFinder from '../Apis/BookFinder'

const BookDetail = (props) => {
  const {isbn} = useParams()
  const {selectedBook, setSelectetBook} = useContext(BookContext)
  const navigate = useNavigate()

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await BookFinder.get(`/${isbn}`);
        setSelectetBook(response.data.data.books)
        console.log(response.data.data.books);
      } catch (err) {
        console.log(err);
      }
    }
    fetchData();
    },[])


  return (
    <form>
      <div className="form-group">
        <label htmlFor="isbn">ISBN</label>
        <output id="isbn" className="form-control" type="text">{selectedBook.isbn}</output>
      </div>

      <div className="form-group">
        <label htmlFor="title">Title</label>
        <output id="title" className="form-control" type="text">{selectedBook.title}</output>
      </div>

      <div className="form-group">
        <label htmlFor="author">Author</label>
        <output id="author" className="form-control" type="text">{selectedBook.author}</output>
      </div>

      <div className="form-group">
        <label htmlFor="year">Year</label>
        <output id="year" className="form-control" type="text">{selectedBook.year}</output>
      </div>

      <div className="form-group">
        <label htmlFor="publisher">Publisher</label>
        <output id="publisher" className="form-control" type="text">{selectedBook.publisher}</output>
      </div>

      <div className="form-group">
        <label htmlFor="language">Langueage</label>
        <output id="language" className="form-control" type="text">{selectedBook.language}</output>
      </div>

      <div className="form-group">
        <label htmlFor="translation">Translation</label>
        <output id="translation" className="form-control" type="text">{selectedBook.translation}</output>
      </div>

      <div className="form-group">
        <label htmlFor="tags">Tags</label>
        <output id="tags" className="form-control" type="text">{selectedBook.tags}</output>
      </div>
     
</form>
  )
}

export default BookDetail
