import React, {useContext, useEffect} from 'react'
import BookFinder from '../Apis/BookFinder';
import { BookContext } from '../cAPI';
import { useNavigate } from 'react-router-dom'

//let navigate = useNavigate

const BookList = (props) => {
  const {books, setBooks} = useContext(BookContext)
  
  const navigate = useNavigate()

  useEffect(() => {
    const fetchdata = async () => {
      try {
        const response = await BookFinder.get("/");
        setBooks(response.data.data.books);
      } catch (err) {}
    }
    fetchdata();
  },[])

  const handleDelete = async (e, isbn) => {
    e.stopPropagation();
    try {
      const response = await BookFinder.delete(`/${isbn}`);
      console.log(response);
      //  setBooks(
      //  books.filter((books) => {
      //  return books.isbn !== isbn;
      //  })
      //  )
    } catch (err) {
      console.log(err);
    }
  }

  return (
    <div className="list-group">
      <div><button onClick={(e) => navigate(`/addbook`)} className="btn btn-danger">Add Book</button></div>
      
      <table className="table table-hover table-dark">
        <thead>
          <tr className="bg-primary">
            <th scope="col">Title</th>
            <th scope="col">Author</th>
            <th scope="col">Year</th>
            <th scope="col">Publisher</th>
            <th scope="col">ISBN</th>
            <th scope="col">Update</th>
            <th scope='col'>Delete</th>
          </tr>
        </thead>
        <tbody>
          {books && books.map((books) => {
            return (
              <tr onClick={() => navigate(`/${books.isbn}`)} key={books.isbn}>
                <td>{books.title}</td>
                <td>{books.author}</td>
                <td>{books.year}</td>
                <td>{books.publisher}</td>
                <td>{books.isbn}</td>
                <td><button onClick={(e) => {e.stopPropagation(), navigate(`/${books.isbn}/update`)}} className="btn btn-warning">Update</button></td>
                <td><button onClick={(e) => handleDelete(e, books.isbn)} className="btn btn-danger">Delete</button></td>       
              </tr>
            )
          })}
        </tbody>
      </table>
    </div>
  )
};

export default BookList
