import React, { useState } from 'react'
import BookFinder from '../Apis/BookFinder'
import { useNavigate } from 'react-router-dom'

const AddBook = (props) => {

    const [isbn, setIsbn] = useState("")
    const [title, setTitle] = useState("")
    const [author, setAuthor] = useState("")
    const [year, setYear] = useState("")
    const [publisher, setPublisher] = useState("")
    const [language, setLanguage] = useState("")
    const [translation, setTranslation] = useState("")
    const [tags, setTags] = useState("")

    const navigate = useNavigate()

    const [form, setForm] = useState("[]")
    const [errors, setErrors] = useState("[]")
    const [errorsClass, setErrorsClass] = useState(["needs-validation"])
    const [feedback, setFeedback] = useState("")
    
    const setField = (field, value) => {
        setForm({
           ...form,
           [field]:value 
        })

        if(!!errors[field]){
            setErrors({
                ...errors,
                [field]:null
            })
        }
    }

    const validateForm = () => {
        const {isbn, title, author, year, publisher, language, translation, tags} = form
        const newErrors = {}

        if(!isbn || isbn === ''){
            newErrors.isbn = 'Please add ISBN'
        }
        if(!title || title === ''){ 
            newErrors.title = 'Please add Title'
        }
        if(!author || author === ''){
            newErrors.author = 'Please add Author'
        }
        if(!year || year === ''){
            newErrors.year = 'Please add Year'
        }
        if(!publisher || publisher === ''){
            newErrors.publisher = 'Please add Publisher'
        }
        if(!language || language === ''){
            newErrors.language = 'Please add Language'
        }
        if(!translation || translation === ''){
            newErrors.translation = 'Please add Translation'
        }
        if(!tags || tags === ''){
            newErrors.tags = 'Please add Tags'
        }

        return newErrors
    } 

    const handleSubmit = async (e) => {
        e.preventDefault();
        const formErrors = validateForm()
        if (Object.keys(formErrors).length > 0){
            setErrors(formErrors)
            console.log(formErrors)
            setErrorsClass("was-validated")
        }else{
            try{
                await BookFinder.post(`/`, {
                    isbn,
                    title,
                    author,
                    year,
                    publisher,
                    language,
                });
            } catch (err) {
                console.log(err);
            }
            navigate("/")
        }
    };

    return (
        <div>
            <form noValidate>

                <div className="mb-3">
                    <label htmlFor="isbn" className="form-label">ISBN</label>
                    <input className="form-control" type="text" 
                    id="isbn"
                    value={form.isbn}
                    onChange={(e) => {setIsbn(e.target.value), setField('isbn', e.target.value)}} 
                    required
                    />
                    <div className="invalid-feedback">{errors.isbn}</div>
                </div>

                <div className="mb-3">
                    <label htmlFor="title" className="form-label">Title</label>
                    <input className="form-control" type="text"
                    id="title"
                    value={form.title}
                    onChange={(e) => {setTitle(e.target.value), setField('title', e.target.value)}}  
                    required
                    />
                    <div className="invalid-feedback">{errors.title}</div>
                </div>
  
                <div className="mb-3">
                    <label htmlFor="author" className="form-label">Author</label>
                    <input className="form-control" type="text"
                    id="author"
                    value={form.author} 
                    onChange={(e) => {setAuthor(e.target.value), setField('author', e.target.value)}}
                    required
                    />
                    <div className="invalid-feedback">{errors.author}</div>
                </div>
          
                <div className="mb-3">
                    <label htmlFor="year" className="form-label">Year</label>
                    <input className="form-control" type="text"
                    id="year"
                    value={form.year}
                    onChange={(e) => {setYear(e.target.value), setField('year', e.target.value)}}
                    required
                    />
                    <div className="invalid-feedback">{errors.year}</div>
                </div>

                <div className="mb-3">
                    <label htmlFor="publisher" className="form-label">Publisher</label>
                    <input className="form-control" type="text"
                    id="publisher"
                    value={form.publisher} 
                    onChange={(e) => {setPublisher(e.target.value), setField('publisher', e.target.value)}}
                    required
                    />
                    <div className="invalid-feedback">{errors.publisher}</div>
                </div>

                <div className="mb-3">
                    <label htmlFor="language" className="form-label">Language</label>
                    <input className="form-control" type="text"
                    id="language"
                    value={form.language}
                    onChange={(e) => {setLanguage(e.target.value), setField('language', e.target.value)}}
                    required
                    />
                    <div className="invalid-feedback">{errors.language}</div>
                </div>

                <div className="mb-3">
                    <label htmlFor="translation" className="form-label">Translation</label>
                    <input className="form-control" type="text"
                    id="translation"
                    value={form.translation}
                    onChange={(e) => {setTranslation(e.target.value), setField('translation', e.target.value)}}
                    required
                    />
                    <div className="invalid-feedback">{errors.translation}</div>
                </div>

                <div className="mb-3">
                    <label htmlFor="tags" className="form-label">Tags</label>
                    <input className="form-control" type="text"
                    id="tags"
                    value={form.tags} 
                    onChange={(e) => {setTags(e.target.value), setField('tags', e.target.value)}}
                    required
                    />
                    <div className="invalid-feedback">{errors.tags}</div>
                </div>

                <div className="d-grid gap-2 d-md-flex justify-content-md-end">
                <button type="button" onClick={(e) => { e.stopPropagation(), navigate(`/`) } } className="btn btn-primary">Back</button>
                <button type="submit" onClick={handleSubmit} className="btn btn-success">Submit</button>
                </div>
            </form>
        </div>
    )
}

export default AddBook
