import React, { useContext, useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import { useNavigate } from 'react-router-dom'
import { BookContext } from '../cAPI';
import BookFinder from '../Apis/BookFinder';

const navigate = useNavigate()

const UpdateBook = (props) => {
    const {isbn} = useParams()
    const {books} = useContext(BookContext)
    const [title, setTitle] = useState("")
    const [author, setAuthor] = useState("")
    const [year, setYear] = useState("")
    const [publisher, setPublisher] = useState("")

    useEffect(() => {
        const fetchdata = async () => {
          try {
            const response = await BookFinder.get(`/${isbn}`);
            console.log(response.data.data.books)
            setTitle(response.data.data.books.title);
            setAuthor(response.data.data.books.author);
            setYear(response.data.data.books.year);
            setPublisher(response.data.data.books.publisher);
          } catch (err) {}
        }
        fetchdata();
      },[])

    const handleSubmit = async (e) => {
        e.preventDefault();
        const updatedRestaurant = await RestaurantFinder.put(`/${isbn}`, {
            title,
            author,
            year,
            publisher,
        });
        navigation();
    };

   const navigation = () => {
        navigate("/");
    }

    return (
        <div>
            <h3>{isbn}</h3>
            <form action="">
                <div className="form-group">
                    <label htmlFor="title">Title</label>
                    <input value={title} onChange={(e) => setTitle(e.target.title)} id="title" className="form-control" type="text" />
                </div>
  
                <div className="form-group">
                    <label htmlFor="author">Author</label>
                    <input value={author} onChange={(e) => setTitle(e.target.author)} id="author" className="form-control" type="text" />
                </div>
          
                <div className="form-group">
                    <label htmlFor="year">Year</label>
                    <input value={year} onChange={(e) => setTitle(e.target.year)} id="year" className="form-control" type="text" />
                </div>

                <div className="form-group">
                    <label htmlFor="publisher">Publisher</label>
                    <input value={publisher} onChange={(e) => setTitle(e.target.publisher)} id="publisher" className="form-control" type="text" />
                </div>
          
                <button type="submit" onClick={handleSubmit} className="btn btn-primary">Submit</button>
            </form>
        </div>
    )
}

export default UpdateBook
