import React, { useState } from 'react'

//const handleSubmit = async (e) => {
//    e.preventDefault();
//    await BookFinder.post(`/`, {
//        isbn,
//        title,
//        author,
//        year,
//        publisher,
//        language,
//        translation,
//        tags,
//    });
//    navigate("/")
//};

const AddBook = () => {
    const {isbn, setIsbn} = useState("")
    const [title, setTitle] = useState("")
    const [author, setAuthor] = useState("")
    const [year, setYear] = useState("")
    const [publisher, setPublisher] = useState("")
    const [language, setLanguage] = useState("")
    const [translation, setTranslation] = useState("")
    const [tags, setTags] = useState("")

    return (
        <div>
            <form action="">
                <div className="form-group">
                    <label htmlFor="isbn">ISBN</label>
                    <input value={isbn} onChange={(e) => setIsbn(e.target.value)} className="form-control" type="text" />
                </div>

                <div className="form-group">
                    <label htmlFor="title">Title</label>
                    <input value={title} onChange={(e) => setTitle(e.target.value)} className="form-control" type="text" />
                </div>
  
                <div className="form-group">
                    <label htmlFor="author">Author</label>
                    <input value={author} onChange={(e) => setAuthor(e.target.value)} className="form-control" type="text" />
                </div>
          
                <div className="form-group">
                    <label htmlFor="year">Year</label>
                    <input value={year} onChange={(e) => setYear(e.target.value)} className="form-control" type="text" />
                </div>

                <div className="form-group">
                    <label htmlFor="publisher">Publisher</label>
                    <input value={publisher} onChange={(e) => setPublisher(e.target.value)} className="form-control" type="text" />
                </div>

                <div className="form-group">
                    <label htmlFor="language">Language</label>
                    <input value={language} onChange={(e) => setLanguage(e.target.value)} className="form-control" type="text" />
                </div>

                <div className="form-group">
                    <label htmlFor="translation">Translation</label>
                    <input value={translation} onChange={(e) => setTranslation(e.target.value)} iclassName="form-control" type="text" />
                </div>

                <div className="form-group">
                    <label htmlFor="tags">Tags</label>
                    <input value={tags} onChange={(e) => setTags(e.target.value)} className="form-control" type="text" />
                </div>

                <button type="submit" onClick={handleSubmit} className="btn btn-primary">Submit</button>
            </form>
        </div>
    )
}

export default AddBook
