import React, { useContext, useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import { BookContext } from '../cAPI';

const UpdateBook = (props) => {
    const {isbn} = useParams()
    const {books} = useContext(BookContext)
    const [title, setTitle] = useState("")
    const [author, setAauthor] = useState("")
    const [year, setYear] = useState("")
    const [publisher, setPublisher] = useState("")

    useEffect(() => {
        const fetchdata = async () => {
          try {
            const response = await BookFinder.get(`/${isbn}`);
            console.log(response.data,data)
            setTitle(response.data.data.title);
            setAuthor(response.data.data.author);
            setYear(response.data.data.year);
            setPublisher(response.data.data.publisher);
          } catch (err) {}
        }
        fetchdata();
      },[])

      useEffect(() => {
        const fetchData = async () => {
          const response = await RestaurantFinder.get(`/${id}`);
          console.log(response.data.data);
          setName(response.data.data.restaurant.name);
          setLocation(response.data.data.restaurant.location);
          setPriceRange(response.data.data.restaurant.price_range);
        };
    
        fetchData();
      }, []);

    console.log (isbn)

    return (
        <div>
            <form action="">
                <div className="form-group">
                    <label htmlFor="title">Title</label>
                    <input value={title} onChange={(e) => setLocation(e.target.title)} id="title" className="form-control" type="text" />
                </div>
  
                <div className="form-group">
                    <label htmlFor="author">Author</label>
                    <input id="author" className="form-control" type="text" />
                </div>
          
                <div className="form-group">
                    <label htmlFor="year">Year</label>
                    <input id="year" className="form-control" type="text" />
                </div>

                <div className="form-group">
                    <label htmlFor="publisher">Publisher</label>
                    <input id="publisher" className="form-control" type="text" />
                </div>
          
                <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        </div>
    )
}

export default UpdateBook
