import React, { useContext, useEffect } from 'react'
import { useParams } from 'react-router-dom'
import { BookContext } from '../cAPI'
import BookFinder from '../Apis/BookFinder'

const BookDetail = () => {
  const {isbn} = useParams()
  const {selectedBook, setSelectetBook} = useContext(BookContext)

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await BookFinder.get(`/${isbn}`);
        console.log(response);
        setSelectetBook(response.data.data);
      } catch (err) {
        console.log(err);
      }
    };

    fetchData();
  }, []);



  return (
    <div>
        <form action="">
            <div className="form-group">
                <label htmlFor="isbn">ISBN</label>
                <input id="isbn" className="form-control" type="text" />
            </div>

            <div className="form-group">
                <label htmlFor="title">Title</label>
                <input value={title} onChange={(e) => setTitle(e.target.value)} id="title" className="form-control" type="text" />
            </div>

            <div className="form-group">
                <label htmlFor="author">Author</label>
                <input value={author} onChange={(e) => setAuthor(e.target.value)} id="author" className="form-control" type="text" />
            </div>
      
            <div className="form-group">
                <label htmlFor="year">Year</label>
                <input value={year} onChange={(e) => setYear(e.target.value)} id="year" className="form-control" type="text" />
            </div>

            <div className="form-group">
                <label htmlFor="publisher">Publisher</label>
                <input value={publisher} onChange={(e) => setPublisher(e.target.value)} id="publisher" className="form-control" type="text" />
            </div>

            <div className="form-group">
                <label htmlFor="language">Language</label>
                <input value={language} onChange={(e) => setLanguage(e.target.value)} id="language" className="form-control" type="text" />
            </div>

            <div className="form-group">
                <label htmlFor="translation">Translation</label>
                <input value={translation} onChange={(e) => setTranslation(e.target.value)} id="translation" className="form-control" type="text" />
            </div>

            <div className="form-group">
                <label htmlFor="tags">Tags</label>
                <input value={tags} onChange={(e) => setTags(e.target.value)} id="tags" className="form-control" type="text" />
            </div>

            <button type="submit" onClick={handleSubmit} className="btn btn-primary">Submit</button>
        </form>
    </div>
  )

}

export default BookDetail
