import React, { useState } from 'react'
import BookFinder from '../Apis/BookFinder'
import { useNavigate } from 'react-router-dom'

const AddBook = (props) => {

    const [isbn, setIsbn] = useState("")
    const [title, setTitle] = useState("")
    const [author, setAuthor] = useState("")
    const [year, setYear] = useState("")
    const [publisher, setPublisher] = useState("")
    const [language, setLanguage] = useState("")
    const [translation, setTranslation] = useState("")
    const [tags, setTags] = useState("")

    const navigate = useNavigate()

    const [form, setForm] = useState("[]")
    const [errors, setErrors] = useState("[]")
    const [errorsClass, setErrorsClass] = useState(['class="row g-3 needs-validation"'])
    
    const setField = (field, value) => {
        setForm({
           ...form,
           [field]:value 
        })

        if(!!errors[field]){
            setErrors({
                ...errors,
                [field]:null
            })
        }
    }

    const validateForm = () => {
        const {isbn, title, author, year, publisher, language, translation, tags} = form
        const newErrors = {}

        if(!isbn || isbn === '') newErrors.isbn = 'Please add ISBN'
        if(!title || title === '') newErrors.title = 'Please add Title'
        if(!author || author === '') newErrors.author = 'Please add Author'
        if(!year || year === '') newErrors.year = 'Please add Year'
        if(!publisher || publisher === '') newErrors.publisher = 'Please add Publisher'
        if(!language || language === '') newErrors.language = 'Please add Language'
        if(!translation || translation === '') newErrors.translation = 'Please add Translation'
        if(!tags || tags === '') newErrors.tags = 'Please add Tags'

        return newErrors
    } 

    const handleSubmit = async (e) => {
        e.preventDefault();
        const formErrors = validateForm()
        if (Object.keys(formErrors).length > 0){
            setErrors(formErrors)
            setErrorsClass('class="was-validated"')
        }else{
            try{
                await BookFinder.post(`/`, {
                    isbn,
                    title,
                    author,
                     year,
                    publisher,
                    language,
                });
            } catch (err) {
                console.log(err);
            }
            navigate("/")
        }
    };

    return (
        <div>
            <form class="was-validated" novalidate>
                <div class="col-md-4">
                    <label for="validationCustom01" class="form-label">First name</label>
                    <input type="text" class="form-control" id="validationCustom01" value="Mark" required />
                    <div class="valid-feedback">
                        Looks good!
                    </div>
                </div>

                <div class="mb-3">
                    <label for="isbn" class="form-label">ISBN</label>
                    <input className="form-control" type="text" 
                    id="isbn"
                    value={form.isbn}
                    isInvalid={!!errors.isbn}
                    onChange={(e) => {setIsbn(e.target.value), setField('isbn', e.target.value)}} 
                    required
                    />
                    <div>{errors.isbn}</div>
                </div>

                <div class="mb-3">
                    <label for="title" class="form-label">Title</label>
                    <input className="form-control" type="text"
                    id="title"
                    value={form.title}
                    isInvalid={!!errors.title} 
                    onChange={(e) => {setTitle(e.target.value), setField('title', e.target.value)}}  
                    required
                    />
                    <div class="valid-feedback">{errors.title}</div>
                </div>
  
                <div class="mb-3">
                    <label for="author" class="form-label">Author</label>
                    <input className="form-control" type="text"
                    id="author"
                    value={form.author}
                    isInvalid={!!errors.author} 
                    onChange={(e) => {setAuthor(e.target.value), setField('author', e.target.value)}}
                    required
                    />
                    <div class="invalid-feedback">{errors.author}</div>
                </div>
          
                <div class="mb-3">
                    <label for="year" class="form-label">Year</label>
                    <input className="form-control" type="text"
                    id="year"
                    value={form.year}
                    isInvalid={!!errors.year} 
                    onChange={(e) => {setYear(e.target.value), setField('year', e.target.value)}}
                    required
                    />
                    <div>{errors.year}</div>
                </div>

                <div class="mb-3">
                    <label for="publisher" class="form-label">Publisher</label>
                    <input className="form-control" type="text"
                    id="publisher"
                    value={form.publisher}
                    isInvalid={!!errors.publisher} 
                    onChange={(e) => {setPublisher(e.target.value), setField('publisher', e.target.value)}}
                    required
                    />
                    <div>{errors.publisher}</div>
                </div>

                <div class="mb-3">
                    <label for="language" class="form-label">Language</label>
                    <input className="form-control" type="text"
                    id="language"
                    value={form.language}
                    isInvalid={!!errors.language} 
                    onChange={(e) => {setLanguage(e.target.value), setField('language', e.target.value)}}
                    required
                    />
                    <div>{errors.language}</div>
                </div>

                <div class="mb-3">
                    <label for="translation" class="form-label">Translation</label>
                    <input className="form-control" type="text"
                    id="translation"
                    value={form.translation}
                    isInvalid={!!errors.translation} 
                    onChange={(e) => {setTranslation(e.target.value), setField('translation', e.target.value)}}
                    required
                    />
                    <div>{errors.translation}</div>
                </div>

                <div class="mb-3">
                    <label for="tags" class="form-label">Tags</label>
                    <input className="form-control" type="text"
                    id="tags"
                    value={form.tags}
                    isInvalid={!!errors.tags} 
                    onChange={(e) => {setTags(e.target.value), setField('tags', e.target.value)}}
                    required
                    />
                    <div>{errors.tags}</div>
                </div>

                <div class="d-grid gap-2 d-md-flex justify-content-md-end">
                <button type="button" onClick={(e) => { e.stopPropagation(), navigate(`/`) } } className="btn btn-primary">Back</button>
                <button type="submit" onClick={handleSubmit} className="btn btn-success">Submit</button>
                </div>
            </form>
        </div>
    )
}

export default AddBook
