import React, { useContext, useEffect } from 'react'
import { useNavigate, useParams } from 'react-router-dom'
import { BookContext } from '../cAPI'
import BookFinder from '../Apis/BookFinder'

const BookDetail = () => {
  const {isbn} = useParams()
  const {selectedBook, setSelectetBook} = useContext(BookContext)
  const navigate = useNavigate()

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await BookFinder.get(`/${isbn}`);
        console.log(response);
        setSelectetBook(response.data.data);
      } catch (err) {
        console.log(err);
      }
    };

    fetchData();
  }, []);



  return (
    <div>
        <form action="">
            <div className="form-group">
                <label htmlFor="isbn">ISBN</label>
                <input id="isbn" className="form-control" type="text" />
            </div>

            <div className="form-group">
                <label htmlFor="title">Title</label>
                <input value={selectedBook.title} id="title" className="form-control" type="text" readOnly />

            </div>

            <div className="form-group">
                <label htmlFor="author">Author</label>
                <input value={selectedBook.author} id="author" className="form-control" type="text" readOnly />
            </div>
      
            <div className="form-group">
                <label htmlFor="year">Year</label>
                <input value={selectedBook.year} id="year" className="form-control" type="text" readOnly />
            </div>

            <div className="form-group">
                <label htmlFor="publisher">Publisher</label>
                <input value={selectedBook.publisher} id="publisher" className="form-control" type="text" readOnly />
            </div>

            <div className="form-group">
                <label htmlFor="language">Language</label>
                <input value={selectedBook.language} id="language" className="form-control" type="text" readOnly />
            </div>

            <div className="form-group">
                <label htmlFor="translation">Translation</label>
                <input value={selectedBook. translation} id="translation" className="form-control" type="text" readOnly />
            </div>

            <div className="form-group">
                <label htmlFor="tags">Tags</label>
                <input value={selectedBook.tags} id="tags" className="form-control" type="text" readOnly />
            </div>

            <button type="submit" onClick={navigate("/")} className="btn btn-primary">Submit</button>
        </form>
    </div>
  )

}

export default BookDetail
